def calculate_floor(data):
    floor = 0
    for char in data:
        if char == '(':
            floor += 1
        elif char == ')':
            floor -= 1

    return floor


def calculate_position_for_basement(data):
    floor = 0
    for index, char in enumerate(data):
        if char == '(':
            floor += 1
        elif char == ')':
            floor -= 1

        if floor == -1:
            return index + 1

    raise Exception('Basement not found')


class TestDay01(unittest.TestCase):

    def test_should_be_floor_0(self):
        self.assertEqual(0, calculate_floor('(())'))
        self.assertEqual(0, calculate_floor('()()'))

    def test_should_be_floor_3(self):
        self.assertEqual(3, calculate_floor('((('))
        self.assertEqual(3, calculate_floor('(()(()('))

    def test_should_be_floor_minus1(self):
        self.assertEqual(-1, calculate_floor('())'))
        self.assertEqual(-1, calculate_floor('))('))

    def test_should_be_floor_minus3(self):
        self.assertEqual(-3, calculate_floor(')))'))
        self.assertEqual(-3, calculate_floor(')())())'))

    def test_should_find_basement_at_position_1(self):
        self.assertEqual(1, calculate_position_for_basement(')'))

    def test_should_find_basement_at_position_5(self):
        self.assertEqual(5, calculate_position_for_basement('()())'))

    def test_part1(self):
        floor = calculate_floor(input)

        print('Part1:', floor)
        self.assertEqual(232, floor)

    def test_part1(self):
        position_of_basement = calculate_position_for_basement(input)

        print('Part2:', position_of_basement)
        self.assertEqual(1783, position_of_basement)


